mapper.update:
UPDATE FILE
SET src_url = #{src_url}
WHERE user_id = #{user_id}
AND type = #{type};

mapper.select:
SELECT src_url
FROM FILE
WHERE user_id = #{user_id}
AND type = #{type};

mapper.update:
UPDATE FILE
SET src_url = NULL
WHERE user_id = #{user_id}
AND type = #{type}

UPDATE FILE
SET src_url = #{src_url}
WHERE mission_id = #{mission_id}
