mapper.select:
SELECT p.post_id, p.content, p.like_count, m.mission_id, DATE_FORMAT(m.mission_date, '%m월 %d일') AS missionDate, e.emotion_id, p.user_id, c.companion_id, p.report_count, p.hidden_YN FROM POST p LEFT JOIN MISSION m ON p.mission_id = m.mission_id LEFT JOIN EMOTION e ON p.emotion_id = e.emotion_id LEFT JOIN COMPANION c ON p.companion_id = c.companion_id LEFT JOIN USER u ON p.user_id = u.user_id WHERE p.companion_id IN
#{companionId}
AND DAY(m.mission_date) = 1
